export type ConfigTypes = {
    defaultConfig: string;
    envConfig: string;
    deploymentConfig: string;
    userConfig: string;
};
export type NonConfigEnv = {
    cwd(): string;
    env: {
        NODE_ENV?: string;
        NODE_CONFIG_TS_ENV?: string;
        DEPLOYMENT?: string;
        USER?: string;
        USERNAME?: string;
        NODE_CONFIG_TS_DIR?: string;
    };
};
export declare const configPaths: <T extends NonConfigEnv>(process: T) => ConfigTypes;
